<style type="text/css">
.rhf-frame {
    display: none;
    min-width: 940px;
    position: relative;
}
.rhf-border {
    border: 1px solid #DDD;
    border-radius: 5px;
    padding: 0px 20px;
}
.rhf-header {
    color: #444;
    font-family: Arial;
    font-size: 20px;
    font-weight: normal;
    padding:10px 10px 3px 10px;
    text-align:left;
    white-space: nowrap;
}
#ybh-text-on {
    display: none;
}
.rhf-fresh-header {
    color: #444;
    font-family: Arial;
    font-size: 20px;
    font-weight: normal;
    white-space: nowrap;
}
.rvi-fresh-footer {
    background-color: rgba(0,0,0,.03);
    padding-left: 35px !important;
    padding-right: 35px !important;
    position: relative;
}
.rvi-fresh-container {
    height: 100px;
    overflow: hidden;
}
.rvi-fresh-container .rhf-RVIs {
    display: inline-block;
    position: relative;
}
.rvi-fresh-container .rhf-RVIs a img {
    box-sizing: border-box;
    vertical-align: middle;
}
.rvi-fresh-container .rhf-faceout-hue {
    background-color: rgba(0,0,0,.03);
    position: absolute;
    left: 0px;
    top: 0px;
    right: 0px;
    bottom: 0px;
    z-index: 100;
}
.ybh-fresh-link a {
    text-decoration: none !important;
}
.ybh-fresh-link {
    color: black !important;
    text-decoration: none !important;
}
.ybh-fresh-link-arrow {
    font-size: 16px;
    vertical-align: bottom;
}
.rhf-loading-outer {
    height: 248px;
    overflow: hidden;
    position: relative;
    margin-top: 10px;
    width: 100% !important;
}
.rhf-loading-outer[class] {
    display: table;
    position: static;
}
.rhf-loading-middle {
    height: 100%;
    width: 100% !important;
}
.rhf-loading-inner {
    text-align: center;
    vertical-align: middle;
}
#rhf-shoveler {
    color: black;
    padding: 10px;
}
.rhf-source-title {
    color: black;
    font-size: 15px;
    font-weight: normal;
}
.rhf-footer {
    padding-bottom: 14px;
}
.rvi-container {
    height: 75px;
    line-height: 75px;
    overflow: hidden;
    padding: 0 10px;
    position: relative;
}
.rvi-container .you-viewed {
    display: inline-block;
    line-height: 13px;
    margin-right: 3px;
    text-align: right;
    width: 45px;        /* this value is overridden in some locales */
    vertical-align: middle;
}
.rvi-container .you-viewed-divider {
    border-left: 1px solid #ddd;
    display: inline-block;
    height: 50px;
    margin-left: 10px;
    vertical-align: middle;
}
#rhf .rhf-border a {
    text-decoration: none;
    color: #004B91;
}
#rhf .rhf-border a:hover {
    color: #E47911;
}
.rvi-container .you-viewed,
.rvi-container #ybh-text-on,
.rvi-container #ybh-text-off,
.rvi-container #ybh-link,
.rhf-sign-in-button {
    color: black;
    font-family: Arial;
    font-size: 13px;
}
.rvi-container #ybh-text-on,
.rvi-container #ybh-text-off {
    padding-left: 40px;
    padding-right: 10px;
}
.rvi-container #ybh-text-on {
    display: none;
}
.rhf-sign-in-tooltip-new-customer {
    font-family: Arial;
    font-size: 11px;
}
.rhf-footer .ybh-edit {
    float: right;
    margin-top: 18px;
    padding-left: 40px;
    padding-right: 10px;
}
.ybh-edit .ybh-edit-arrow,
.ybh-edit .ybh-edit-link {
    display: inline-block;
    font-family: Arial;
    font-weight: bold;
    line-height: 11px;
    vertical-align: top;
}
.ybh-edit .ybh-edit-arrow {
    color: rgb(204, 153, 0);
    font-size: 11px;
    margin-top: -2px;
    padding-right: 8px;
}
.ybh-edit .ybh-edit-link {
    font-size: 10px;
    width: 80px;
}
#rhf a:hover .title,
#rhf a:active .title {
    text-decoration: underline;
}
.rhf-state-signin {
    margin-top: 19px;
    margin-bottom: 17px;
}
.rhf-sign-in-button-box {
    float: right;
    border-left: 1px solid #ddd;
    margin-right: -30px;
    text-align: center;
    white-space: nowrap;
}
.rhf-sign-in-button {
    border: 0;
    line-height: 15px;
    margin: 0;
    padding-left: 20px;
    padding-right: 20px;
}
.rhf-sign-in-button .action-button,
.rhf-sign-in-button a.action-button:hover,
.rhf-sign-in-button a.action-button:link,
.rhf-sign-in-button a.action-button:active {
    max-width: 230px;
    text-decoration: none;
    margin-bottom: 5px;
    margin-top: 5px;
    width: 230px;
    display:block;
    background-position: -10px -170px;
    background-image: url("http://g-ecx.images-amazon.com/images/G/01/gno/beacon/BeaconSprite-US-01._V397411194_.png");
}
.rhf-sign-in-button .action-inner,
a.action-button:link .action-inner,
a.action-button:active .action-inner,
a.action-button:hover .action-inner,
a.action-button:visited .action-inner {
    cursor: pointer;
    background-position: right -170px;
    display: block;
    height: 28px;
    position: relative;
    color: black;
    font-size: 12px;
    line-height: 28px;
    text-align: center;
    font-family: "arial","sans-serif";
    font-weight: bold;
    text-decoration: none;
    text-shadow: 0px 1px 0px #ffe093;
}
.rvi-container .no-rvi-message {
    color: black;
    float: left;
    font-size: 13px;
    font-style: italic;
}
.rhf-divider {
    border-top: 1px solid #DDD;
    height: 23px;
    margin-bottom: -6px;
    background: -moz-linear-gradient(top,#ddd,#f7f7f7 3px,white);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#ddd),color-stop(3px,#f7f7f7),color-stop(100%,white));
    background: -webkit-linear-gradient(top,#ddd,#f7f7f7 3px,white);
    background: -o-linear-gradient(top,#ddd,#f7f7f7 3px,white);
    background: -ms-linear-gradient(top,#ddd,#f7f7f7 3px,white);
    background: linear-gradient(top,#ddd,#f7f7f7 3px,white);
    filter: none;
    z-index: 0;
    zoom: 1;
}
.rhf-divider:after {
    display: block;
    width: 100%;
    height: 44px;
    background-color: transparent;
    background: -moz-linear-gradient(left,white,rgba(255,255,255,0),white);
    background: -webkit-gradient(linear,left top,right top,color-stop(0%,white),color-stop(50%,rgba(255,255,255,0)),color-stop(100%,white));
    background: -webkit-linear-gradient(left,white,rgba(255,255,255,0),white);
    background: -o-linear-gradient(left,white,rgba(255,255,255,0),white);
    background: -ms-linear-gradient(left,white,rgba(255,255,255,0),white);
    background: linear-gradient(left,white,rgba(255,255,255,0),white);
    filter: none;
    z-index: 1;
    content: "";
}
.rvi-container #ybh-link a {
    cursor: pointer;
    font-size: 13px;
}
.rvi-container .rhf-RVIs {
    display: inline-block;
    padding: 0px 10px 0px 10px;
    vertical-align: middle;
}
.rvi-container .rhf-RVIs a img {
    border: 1px solid #DDD;
    box-sizing: border-box;
    -moz-box-sizing: border-box; /* FireFox */
    -webkit-box-sizing: border-box; /* Safari */
    vertical-align: middle;
}
.rvi-container .rhf-RVIs a:hover img {
    border: 2px solid #E47911;
}
#rhf-container #rhf-recs-down-text {
    padding-top: 13px;
    font-size: 16px;
    line-height: 26px;
}
#rhf-container #rhfNoRecsMessage {
    color: #666666;
    font-size: 13px;
    font-family: Arial;
}
* html .rhf {
    height:1%;
}
